import{_ as n,c as a,o as s,a as t}from"./app.9dbb06c5.js";const m='{"title":"Gulp \u6D41","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u53C2\u8003\u94FE\u63A5","slug":"\u53C2\u8003\u94FE\u63A5"}],"relativePath":"FE-Engineering/ci-gulp/gulp_stream.md"}',e={},p=t(`<h1 id="gulp-\u6D41" tabindex="-1">Gulp \u6D41 <a class="header-anchor" href="#gulp-\u6D41" aria-hidden="true">#</a></h1><p><code>Gulp</code>\u662F\u4E00\u4E2A\u57FA\u4E8E\u6D41<code>Stream</code>\u7684\u81EA\u52A8\u5316\u6784\u5EFA\u5DE5\u5177\uFF0C\u5176\u4E2D\u7684\u6D41<code>Stream</code>\u4E5F\u5C31\u662F<code>node stream</code>\u3002</p><p><strong>\u6D41 Stream \u662F\u4EC0\u4E48\uFF1F</strong></p><p>\u6D41\u4E0D\u662F Node.js \u7279\u6709\u7684\u6982\u5FF5\u3002 \u5B83\u4EEC\u662F\u51E0\u5341\u5E74\u524D\u5728 Unix \u64CD\u4F5C\u7CFB\u7EDF\u4E2D\u5F15\u5165\u7684\uFF0C\u7A0B\u5E8F\u53EF\u4EE5\u901A\u8FC7\u7BA1\u9053\u8FD0\u7B97\u7B26\uFF08<code>|</code>\uFF09\u5BF9\u6D41\u8FDB\u884C\u4EA4\u4E92\uFF0C\u800C node.js \u53EF\u4EE5\u901A\u8FC7 <code>.pipe()</code>\u65B9\u6CD5\u5BF9\u6D41\u8FDB\u884C\u4EA4\u4E92\u3002</p><div class="language-js"><pre><code><span class="token comment">// unix \u5E38\u89C1\u4E8E\u547D\u4EE4\u884C\u64CD\u4F5C\u4E2D</span>
a <span class="token operator">|</span> b <span class="token operator">|</span> c

<span class="token comment">// node stream</span>
a<span class="token punctuation">.</span><span class="token function">pipe</span><span class="token punctuation">(</span>b<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">pipe</span><span class="token punctuation">(</span>c<span class="token punctuation">)</span>

<span class="token comment">// gulp stream</span>
gulp<span class="token punctuation">.</span><span class="token function">src</span><span class="token punctuation">(</span><span class="token string">&#39;source.path&#39;</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">pipe</span><span class="token punctuation">(</span>gulp<span class="token punctuation">.</span><span class="token function">dest</span><span class="token punctuation">(</span><span class="token string">&#39;destination.path&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre></div><p><strong>\u6D41\u89E3\u51B3\u4E86\u4EC0\u4E48\u95EE\u9898\uFF1F</strong></p><p>\u5982\u679C\u4E0D\u7528\u6D41\uFF0C\u5728\u4F20\u7EDF\u7684\u65B9\u5F0F\u4E2D\uFF0C\u5F53\u544A\u8BC9\u7A0B\u5E8F\u8BFB\u53D6\u6587\u4EF6\u65F6\uFF0C\u4F1A\u5C06\u6587\u4EF6\u4ECE\u5934\u5230\u5C3E\u8BFB\u5165\u5185\u5B58\uFF0C\u7136\u540E\u8FDB\u884C\u5904\u7406\u3002</p><p>\u4F46\u5982\u679C\u4F7F\u7528\u6D41\uFF0C\u5219\u53EF\u4EE5\u9010\u4E2A\u7247\u6BB5\u5730\u8BFB\u53D6\u5E76\u5904\u7406\uFF0C\u800C\u65E0\u9700\u5C06\u5168\u90E8\u5185\u5BB9\u5168\u90E8\u4FDD\u5B58\u5728\u5185\u5B58\u4E2D\u540E\uFF0C\u518D\u5904\u7406\u3002</p><p>\u4E00\u4E2A\u5178\u578B\u7684\u4F8B\u5B50\u662F\u4ECE\u78C1\u76D8\u8BFB\u53D6\u6587\u4EF6\uFF1A</p><div class="language-js"><pre><code><span class="token keyword">const</span> http <span class="token operator">=</span> <span class="token function">require</span><span class="token punctuation">(</span><span class="token string">&#39;http&#39;</span><span class="token punctuation">)</span>
<span class="token keyword">const</span> fs <span class="token operator">=</span> <span class="token function">require</span><span class="token punctuation">(</span><span class="token string">&#39;fs&#39;</span><span class="token punctuation">)</span>

<span class="token keyword">const</span> server <span class="token operator">=</span> http<span class="token punctuation">.</span><span class="token function">createServer</span><span class="token punctuation">(</span><span class="token keyword">function</span><span class="token punctuation">(</span><span class="token parameter">req<span class="token punctuation">,</span> res</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
  <span class="token comment">/**
   * \u4E00\u3001\u4F20\u7EDF\u65B9\u5F0F\uFF1AreadFile() \u8BFB\u53D6\u6587\u4EF6\u7684\u5168\u90E8\u5185\u5BB9\uFF0C\u5E76\u5728\u5B8C\u6210\u65F6\u8C03\u7528\u56DE\u8C03\u51FD\u6570,\u5C06\u6587\u4EF6\u5185\u5BB9\u54CD\u5E94\u7ED9\u5BA2\u6237\u7AEF
   * \u5982\u679C\u6587\u4EF6\u5F88\u5927\uFF0C\u5219\u8BE5\u64CD\u4F5C\u4F1A\u82B1\u8D39\u8F83\u591A\u7684\u65F6\u95F4
   */</span>
  fs<span class="token punctuation">.</span><span class="token function">readFile</span><span class="token punctuation">(</span>__dirname <span class="token operator">+</span> <span class="token string">&#39;/data.txt&#39;</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token parameter">err<span class="token punctuation">,</span> data</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
    res<span class="token punctuation">.</span><span class="token function">end</span><span class="token punctuation">(</span>data<span class="token punctuation">)</span>
  <span class="token punctuation">}</span><span class="token punctuation">)</span>
  
  <span class="token comment">/**
   * \u4E8C\u3001\u4F7F\u7528 stream
   * \u5F53\u8981\u53D1\u9001\u7684\u6570\u636E\u5757\u5DF2\u83B7\u5F97\u65F6\u5C31\u7ACB\u5373\u5F00\u59CB\u5C06\u5176\u6D41\u5F0F\u4F20\u8F93\u5230 HTTP \u5BA2\u6237\u7AEF\uFF0C\u800C\u4E0D\u662F\u7B49\u5F85\u76F4\u5230\u6587\u4EF6\u88AB\u5B8C\u5168\u8BFB\u53D6\u3002
   */</span>
  <span class="token keyword">const</span> stream <span class="token operator">=</span> fs<span class="token punctuation">.</span><span class="token function">createReadStream</span><span class="token punctuation">(</span>__dirname <span class="token operator">+</span> <span class="token string">&#39;/data.txt&#39;</span><span class="token punctuation">)</span>
  stream<span class="token punctuation">.</span><span class="token function">pipe</span><span class="token punctuation">(</span>res<span class="token punctuation">)</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
server<span class="token punctuation">.</span><span class="token function">listen</span><span class="token punctuation">(</span><span class="token number">3000</span><span class="token punctuation">)</span>
</code></pre></div><p>\u6240\u4EE5\uFF0C\u6D41 Stream \u4E3A node \u5E94\u7528\u7A0B\u5E8F\u4E86\u4E00\u79CD\u4EE5\u9AD8\u6548\u7684\u65B9\u5F0F\u5904\u7406\u8BFB/\u5199\u6587\u4EF6\u3001\u7F51\u7EDC\u901A\u4FE1\u3001\u6216\u4EFB\u4F55\u7C7B\u578B\u7684\u7AEF\u5230\u7AEF\u7684\u4FE1\u606F\u4EA4\u6362\u7684\u89E3\u51B3\u65B9\u6848\u3002</p><p>\u4F7F\u7528\u6D41\u5904\u7406\u6570\u636E\u7684\u4E24\u4E2A\u4F18\u70B9\uFF1A</p><ul><li><strong>\u5185\u5B58\u6548\u7387</strong>: \u65E0\u9700\u52A0\u8F7D\u5927\u91CF\u7684\u6570\u636E\u5230\u5185\u5B58\u4E2D\u5373\u53EF\u8FDB\u884C\u5904\u7406\u3002</li><li><strong>\u65F6\u95F4\u6548\u7387</strong>: \u5F53\u83B7\u5F97\u6570\u636E\u4E4B\u540E\u5373\u53EF\u7ACB\u5373\u5F00\u59CB\u5904\u7406\u6570\u636E\uFF0C\u8FD9\u6837\u6240\u9700\u7684\u65F6\u95F4\u66F4\u5C11\uFF0C\u800C\u4E0D\u5FC5\u7B49\u5230\u6574\u4E2A\u6570\u636E\u6709\u6548\u8D1F\u8F7D\u53EF\u7528\u624D\u5F00\u59CB\u3002</li></ul><p><strong>\u7406\u89E3\u6D41 Stream \u548C\u7F13\u51B2\u533A Buffer</strong></p><p><a href="http://fer123.gitee.io/Backend/Node/bit-byte-stream-buffer.html#%E6%B5%81-stream" target="_blank" rel="noopener noreferrer">\u6D41 Stream \u548C\u7F13\u51B2\u533A Buffer**</a></p><p><code>Stream</code></p><p>\u5728\u8BA1\u7B97\u673A\u5B58\u50A8\u7684\u6570\u636E\u5E76\u4E0D\u662F\u6B7B\u7684\uFF0C\u56FA\u5B9A\u7684\uFF0C\u6211\u4EEC\u59CB\u7EC8\u4F1A\u9891\u7E41\u5730\u5BF9\u5176\u8FDB\u884C\u64CD\u4F5C\uFF0C\u6BD4\u5982\u8BFB\u53D6\u5199\u5165\u3001\u5B58\u50A8\u4F4D\u7F6E\u7684\u590D\u5236\u3001\u526A\u7C98\u7B49\uFF0C\u672C\u8D28\u4E0A\u90FD\u662F\u5C06\u67D0\u4E2A\u4F4D\u7F6E\u7684\u4E00\u5806\u4E8C\u8FDB\u5236\u6570\u636E\u79FB\u52A8\u5230\u53E6\u4E00\u4E2A\u4F4D\u7F6E\u3002\u597D\u6BD4\u6211\u4EEC\u628A\u6C34\u4ECE\u4E00\u4E2A\u676F\u5B50\u5012\u5230\u53E6\u4E00\u4E2A\u676F\u5B50\uFF0C\u5C31\u4F1A\u5F62\u6210\u6C34\u6D41\u4E00\u6837\uFF0C\u79FB\u52A8\u4E8C\u8FDB\u5236\u6570\u636E\u4ECE\u4E00\u4E2A\u5730\u65B9\u5230\u53E6\u4E00\u4E2A\u5730\u65B9\u5C31\u4F1A\u5F62\u6210\u4E00\u4E2A\u6570\u636E\u6D41\u52A8\u7684\u8FC7\u7A0B\uFF0C\u5F62\u8C61\u5730\u6BD4\u55BB\u4E3A\u4E8C\u8FDB\u5236\u6570\u636E\u6D41\u3002\u5B9E\u9645\u4E0A\uFF0C\u5DE8\u578B\u6570\u636E\u4F1A\u88AB\u5206\u5272\u6210\u4E00\u5C0F\u5757\u4E00\u5C0F\u5757\u5730(chunks)\u8FDB\u884C\u4F20\u8F93\uFF0C\u4E5F\u5C31\u5F62\u6210\u4E86\u6D41 Stream\u3002</p><p><code>Buffer</code></p><p>\u5728\u8BA1\u7B97\u673A\u5404\u4E2A\u90E8\u4EF6\u7684\u5904\u7406\u901F\u5EA6\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u6838\u5FC3CPU\u8BA1\u7B97\u662F\u6700\u5FEB\uFF0C\u5185\u5B58\u4E2D\u6570\u636E\u7684\u8BFB\u53D6\u548C\u5199\u5165\u901F\u5EA6\u6BD4\u786C\u76D8\u5FEB\uFF0C\u786C\u76D8\u7684\u8BFB\u53D6\u548C\u5199\u5165\u901F\u5EA6\u6BD4U\u76D8\u5FEB\u3002\u90A3\u8FD9\u4E2A\u5199\u5165\u548C\u8BFB\u53D6\u65F6\u597D\u6BD4\u6C34\u7BA1\u7684\u4E24\u7AEF\uFF0C\u5F53\u6C34\u6D41\u51FA\u7684\u901F\u5EA6\u6BD4\u6C34\u6CE8\u5165\u7684\u901F\u5EA6\u5FEB\uFF0C\u90A3\u6C34\u7BA1\u4E2D\u95F4\u80AF\u5B9A\u6709\u4E00\u88C1\u7A7A\u4E86\uFF0C\u6C34\u6D41\u51FA\u7AEF\u5C31\u8981\u7B49\u5F85\u3002\u5F53\u6C34\u6CE8\u5165\u7684\u901F\u5EA6\u6BD4\u6C34\u6D41\u51FA\u7684\u901F\u5EA6\u5FEB\uFF0C\u90A3\u6C34\u80AF\u5B9A\u4F1A\u5728\u5165\u53E3\u6EA2\u51FA\uFF0C\u8FD9\u65F6\u5FC5\u987B\u51CF\u6162\u6C34\u6CE8\u5165\u7684\u901F\u5EA6\u3002\u5728\u8BA1\u7B97\u673A\u4E2D\u5F53\u4E24\u7AEF\u901F\u5EA6\u4E0D\u5339\u914D\u65F6\uFF0C\u65E0\u6548\u7684\u7B49\u5F85\u662F\u6D6A\u8D39\u6027\u80FD\uFF0C\u6211\u672C\u53EF\u4EE5\u5728\u7B49\u5F85\u65F6\u95F4\u5185\u5904\u7406\u66F4\u591A\u4E8B\uFF0C\u800C\u73B0\u5728\u95F2\u4E0B\u6765\uFF0C\u8FD9\u662F\u8981\u4F18\u5316\u7684\u5730\u65B9\u3002</p><p>\u90A3\u4F18\u5316\u7684\u65B9\u6848\u5C31\u662F\u8BBE\u7F6E\u4E00\u4E2A\u7F13\u5B58\u51B2\u6765\u5339\u914D\u4E24\u7AEF\u7684\u901F\u5EA6\u3002\u6BD4\u5982\u6CE8\u6C34\u548C\u63A5\u6C34\u4E2D\u95F4\u8BBE\u4E00\u4E2A\u4E00\u5B9A\u5BB9\u91CF\u7684\u6C34\u69FD\u6765\u7F13\u51B2\uFF0C\u6211\u53EF\u4EE5\u4FDD\u6301\u9AD8\u901F\u8FC5\u901F\u628A\u6C34\u69FD\u88C5\u6EE1\uFF0C\u4F46\u6C34\u69FD\u7684\u6C34\u6D41\u51FA\u901F\u5EA6\u53EF\u4EE5\u5339\u914D\u6C34\u7BA1\u6D41\u51FA\u7684\u901F\u5EA6\u3002\u8FD9\u6837\u4E24\u7AEF\u90FD\u4E0D\u803D\u8BEF\u65F6\u95F4\u3002</p><p>\u8FD9\u4E2A\u6C34\u69FD\u5C31\u662F\u4E2A\u6C34\u6D41\u7684\u7F13\u51B2\u533A\uFF0C\u800CBuffer\u5C31\u662FStream\u6D41\u4F20\u8F93\u8FC7\u7A0B\u7684\u7F13\u51B2\u533A\u3002Buffer\u5C31\u662F\u4F60\u7535\u8111\u4E0A\u7684\u4E00\u4E2A\u5F88\u5C0F\u7684\u7269\u7406\u5185\u5B58\uFF0C\u4E00\u822C\u5728RAM\u4E2D\uFF0C\u5728\u8FD9\u91CC\u6570\u636E\u6682\u65F6\u7684\u5B58\u50A8\u3001\u7B49\u5F85\uFF0C\u6700\u540E\u53D1\u9001\u8FC7\u53BB\u5E76\u5904\u7406\uFF0Cnode\u4E2D\u9ED8\u8BA4\u5927\u5C0F\u662F8kb\uFF0C\u4F46\u4E00\u822C\u521B\u5EFA\u4E00\u4E2Abuffer\u4F1A\u6307\u5B9A\u5927\u5C0F\uFF0C\u6216\u6839\u636E\u4F20\u5165\u5B57\u7B26\u5339\u914D\u5927\u5C0F\uFF0C\u5F53\u8D85\u51FA8kb\u4F1A\u62A5\u9519\u3002</p><p>\u4E00\u4E2A\u5173\u4E8Ebuffer\u5F88\u5178\u578B\u7684\u4F8B\u5B50\uFF0C\u5C31\u662F\u4F60\u5728\u7EBF\u770B\u89C6\u9891\u7684\u65F6\u5019\u3002\u5982\u679C\u4F60\u7684\u7F51\u7EDC\u8DB3\u591F\u5FEB\uFF0C\u6570\u636E\u6D41(stream)\u5C31\u53EF\u4EE5\u8DB3\u591F\u5FEB\uFF0C\u53EF\u4EE5\u8BA9buffer\u8FC5\u901F\u586B\u6EE1\u7136\u540E\u53D1\u9001\u548C\u5904\u7406\uFF0C\u7136\u540E\u5904\u7406\u53E6\u4E00\u4E2A\uFF0C\u518D\u53D1\u9001\uFF0C\u518D\u53E6\u4E00\u4E2A\uFF0C\u518D\u53D1\u9001\uFF0C\u7136\u540E\u6574\u4E2Astream\u5B8C\u6210\u3002</p><p>\u4F46\u662F\u5F53\u4F60\u7F51\u7EDC\u8FDE\u63A5\u5F88\u6162\uFF0C\u5F53\u5904\u7406\u5B8C\u5F53\u524D\u7684\u6570\u636E\u540E\uFF0C\u4F60\u7684\u64AD\u653E\u5668\u5C31\u4F1A\u6682\u505C\uFF0C\u6216\u51FA\u73B0\u201D\u7F13\u51B2\u201D(buffer)\u5B57\u6837\uFF0C\u610F\u601D\u662F\u6B63\u5728\u6536\u96C6\u66F4\u591A\u7684\u6570\u636E\uFF0C\u6216\u8005\u7B49\u5F85\u66F4\u591A\u7684\u6570\u636E\u5230\u6765\uFF0C\u624D\u80FD\u4E0B\u4E00\u6B65\u5904\u7406\u3002\u5F53buffer\u88C5\u6EE1\u5E76\u5904\u7406\u597D\uFF0C\u64AD\u653E\u5668\u5C31\u4F1A\u663E\u793A\u6570\u636E\uFF0C\u4E5F\u5C31\u662F\u64AD\u653E\u89C6\u9891\u4E86\u3002\u5728\u64AD\u653E\u5F53\u524D\u5185\u5BB9\u7684\u65F6\u5019\uFF0C\u66F4\u591A\u7684\u6570\u636E\u4E5F\u4F1A\u6E90\u6E90\u4E0D\u65AD\u7684\u4F20\u8F93\u3001\u5230\u8FBE\u548C\u5728buffer\u7B49\u5F85\u3002</p><p>\u5982\u679C\u64AD\u653E\u5668\u5DF2\u7ECF\u5904\u7406\u5B8C\u6216\u64AD\u653E\u5B8C\u524D\u4E00\u4E2A\u6570\u636E\uFF0Cbuffer\u4ECD\u7136\u6CA1\u6709\u586B\u6EE1\uFF0C\u201Dbuffering\u201D(\u7F13\u51B2)\u5B57\u7B26\u5C31\u4F1A\u518D\u6B21\u51FA\u73B0\uFF0C\u7B49\u5F85\u548C\u6536\u96C6\u66F4\u591A\u7684\u6570\u636E\u3002</p><p>\u8FD9\u5C31\u662F<strong>Buffer\uFF01</strong></p><blockquote><p>\u5728 node \u4E2D\u5F53\u5F00\u542F\u4E00\u4E2A\u6D41 stream \u65F6\uFF0Cnode\u4F1A\u81EA\u52A8\u521B\u5EFA buffer \u3002Buffer \u91CC\u88C5\u7740\u4E8C\u8FDB\u5236\u6570\u636E\uFF0C\u6240\u4EE5\u5728 node \u4E2D\u4E5F\u4E3ABuffer \u63D0\u4F9B\u4E86 API \u6765\u4E0E\u7F13\u51B2\u533A Buffer \u4E2D\u4E8C\u8FDB\u5236\u6570\u636E\u8FDB\u884C\u4EA4\u4E92\u548C\u64CD\u4F5C\u3002\u6BD5\u7ADF buffer \u5C31\u662F\u4E00\u5C0F\u5757\u5185\u5B58\u7A7A\u95F4\u561B\u3002</p></blockquote><h3 id="\u53C2\u8003\u94FE\u63A5" tabindex="-1">\u53C2\u8003\u94FE\u63A5 <a class="header-anchor" href="#\u53C2\u8003\u94FE\u63A5" aria-hidden="true">#</a></h3><ul><li><a href="http://nodejs.cn/learn/nodejs-streams" target="_blank" rel="noopener noreferrer">Node Stream</a></li><li><a href="https://github.com/jabez128/stream-handbook" target="_blank" rel="noopener noreferrer">stream-handbook \u4E2D\u6587\u624B\u518C</a></li><li><a href="https://www.cnblogs.com/vajoy/p/6349817.html" target="_blank" rel="noopener noreferrer">gulp\u6E90\u7801\u89E3\u6790\uFF08\u4E00\uFF09\u2014\u2014 Stream\u8BE6\u89E3</a></li><li><a href="http://fer123.gitee.io/Backend/Node/stream.html#%E6%B5%81%E7%9A%84%E7%B1%BB%E5%9E%8B" target="_blank" rel="noopener noreferrer">fer123 blog: node stream</a></li></ul>`,28),o=[p];function c(r,u,l,i,k,d){return s(),a("div",null,o)}var g=n(e,[["render",c]]);export{m as __pageData,g as default};
