import{_ as e,c as i,o as n,a as t}from"./app.fa011131.js";var a="data:image/png;base64,";const u='{"title":"EditorConfig","description":"","frontmatter":{},"headers":[{"level":2,"title":".editorconfig \u914D\u7F6E\u6587\u4EF6","slug":"editorconfig-\u914D\u7F6E\u6587\u4EF6"},{"level":3,"title":"\u5339\u914D\u6A21\u5F0F","slug":"\u5339\u914D\u6A21\u5F0F"},{"level":3,"title":"\u5C5E\u6027","slug":"\u5C5E\u6027"},{"level":2,"title":"\u6CE8\u91CA","slug":"\u6CE8\u91CA"},{"level":2,"title":"\u5B98\u7F51\u793A\u4F8B","slug":"\u5B98\u7F51\u793A\u4F8B"},{"level":2,"title":"VS Code \u4F7F\u7528","slug":"vs-code-\u4F7F\u7528"},{"level":2,"title":"\u53C2\u8003\u94FE\u63A5","slug":"\u53C2\u8003\u94FE\u63A5"}],"relativePath":"FE-Engineering/dev-linter/EditorConfig.md"}',o={},r=t('<h1 id="editorconfig" tabindex="-1">EditorConfig <a class="header-anchor" href="#editorconfig" aria-hidden="true">#</a></h1><p><img src="'+a+`" alt=""></p><p><div class="table-of-contents"><ul><li><a href="#editorconfig-\u914D\u7F6E\u6587\u4EF6">.editorconfig \u914D\u7F6E\u6587\u4EF6</a><ul><li><a href="#\u5339\u914D\u6A21\u5F0F">\u5339\u914D\u6A21\u5F0F</a></li><li><a href="#\u5C5E\u6027">\u5C5E\u6027</a></li></ul></li><li><a href="#\u6CE8\u91CA">\u6CE8\u91CA</a></li><li><a href="#\u5B98\u7F51\u793A\u4F8B">\u5B98\u7F51\u793A\u4F8B</a></li><li><a href="#vs-code-\u4F7F\u7528">VS Code \u4F7F\u7528</a></li><li><a href="#\u53C2\u8003\u94FE\u63A5">\u53C2\u8003\u94FE\u63A5</a></li></ul></div></p><p>EditorConfig \u548C Prettier \u4E00\u6837\uFF0C\u90FD\u662F\u7528\u6765\u683C\u5F0F\u5316\u4EE3\u7801\u7684\u3002\u4F46 EditorConfig \u7684\u4F5C\u7528\u66F4\u4FA7\u91CD\u4E8E\u5E2E\u52A9\u5F00\u53D1\u4EBA\u5458\u5728\u4E0D\u540C\u7684\u7F16\u8F91\u5668\u6216 IDE \u4E4B\u95F4\u7EDF\u4E00\u4EE3\u7801\u98CE\u683C\u3002\u4F9D\u636E\u9879\u76EE\u4E2D .editorconfig \u914D\u7F6E\u6587\u4EF6\u7684\u89C4\u5219\u8986\u76D6\u5230\u7F16\u7801\u5668\u9ED8\u8BA4\u7684\u98CE\u683C\u6765\u8FBE\u5230\u4EE3\u7801\u98CE\u683C\u7684\u7EDF\u4E00\u3002</p><p>\u8981\u4F7F\u7528 EditorConfig \u5B8C\u6210\u4EE3\u7801\u98CE\u683C\u7EDF\u4E00\uFF0C\u9700\u8981\u4E24\u90E8\u5206\u5DE5\u4F5C\uFF1A</p><ul><li>.editorConfig \u914D\u7F6E\u6587\u4EF6</li><li>\u6709\u90E8\u5206\u7F16\u8BD1\u5668\u539F\u751F\u652F\u6301\u8BFB\u53D6 .editorconfig \u914D\u7F6E\u6587\u4EF6\uFF0C\u4F46\u53E6\u4E00\u90E8\u5206\u7F16\u8BD1\u5668\u9700\u8981\u5B89\u88C5\u76F8\u5E94\u7684\u63D2\u4EF6\u6765\u8BC6\u522B\u914D\u7F6E\u6587\u4EF6\u3002\u6BD4\u5982 VS Code \u9700\u8981\u5B89\u88C5 <code>EditorConfig for VS Code</code>\uFF0C\u7136\u540E\u5728\u9879\u76EE\u4EFB\u4E00\u76EE\u5F55\u4E0B\u53F3\u952E\u70B9\u51FB<code>Generate .editorconfig</code>\uFF0C\u53EF\u4EE5\u76F4\u63A5\u751F\u6210 .editorconfig \u6587\u4EF6\u3002</li></ul><h2 id="editorconfig-\u914D\u7F6E\u6587\u4EF6" tabindex="-1">.editorconfig \u914D\u7F6E\u6587\u4EF6 <a class="header-anchor" href="#editorconfig-\u914D\u7F6E\u6587\u4EF6" aria-hidden="true">#</a></h2><p>.editorconfig \u914D\u7F6E\u6587\u4EF6\u4E2D\u4E3B\u8981\u7531\u4E24\u90E8\u5206\u5185\u5BB9\u7EC4\u6210</p><ul><li>\u901A\u914D\u7B26\u7EC4\u6210\u7684\u6587\u4EF6\u5339\u914D\u6A21\u5F0F</li><li>\u6307\u5B9A\u4EE3\u7801\u683C\u5F0F\u7684\u5C5E\u6027\u503C</li></ul><p>editorConfig \u914D\u7F6E\u6587\u4EF6\u9700\u8981\u662F UTF-8 \u5B57\u7B26\u96C6\u7F16\u7801\u7684,\u4E00\u884C\u5B9A\u4E49\u4E00\u4E2A\u89C4\u5219\uFF0C\u4EE5\u56DE\u8F66\u6362\u884C\u6216\u6362\u884C\u4F5C\u4E3A\u4E00\u884C\u7684\u5206\u9694\u7B26\u3002</p><h3 id="\u5339\u914D\u6A21\u5F0F" tabindex="-1">\u5339\u914D\u6A21\u5F0F <a class="header-anchor" href="#\u5339\u914D\u6A21\u5F0F" aria-hidden="true">#</a></h3><p>\u5339\u914D\u6A21\u5F0F\u4EE5<code>[ ]</code>\u4E2D\u62EC\u53F7\u5305\u88F9\uFF0C\u4E2D\u62EC\u53F7\u5185\u90E8\u4EE5\u901A\u914D\u7B26\u6A21\u5F0F\u5B9A\u4E49\u5339\u914D\u89C4\u5219\uFF0C\u5176\u4E2D\u659C\u7EBF(/)\u88AB\u7528\u4F5C\u4E3A\u4E00\u4E2A\u8DEF\u5F84\u5206\u9694\u7B26</p><div class="language-"><pre><code>*                \u5339\u914D\u9664/\u4E4B\u5916\u7684\u4EFB\u610F\u5B57\u7B26\u4E32
**               \u5339\u914D\u4EFB\u610F\u5B57\u7B26\u4E32
?                \u5339\u914D\u4EFB\u610F\u5355\u4E2A\u5B57\u7B26
[name]           \u5339\u914Dname\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A\u5355\u4E00\u5B57\u7B26
[!name]          \u5339\u914D\u4E0D\u5B58\u5728name\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A\u5355\u4E00\u5B57\u7B26
{s1,s2,s3}       \u5339\u914D\u7ED9\u5B9A\u7684\u5B57\u7B26\u4E32\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A(\u7528\u9017\u53F7\u5206\u9694)
{num1..num2}   \u3000\u5339\u914Dnum1\u5230num2\u4E4B\u95F4\u7684\u4EFB\u610F\u4E00\u4E2A\u6574\u6570, \u8FD9\u91CC\u7684num1\u548Cnum2\u53EF\u4EE5\u4E3A\u6B63\u6574\u6570\u4E5F\u53EF\u4EE5\u4E3A\u8D1F\u6574\u6570
</code></pre></div><h3 id="\u5C5E\u6027" tabindex="-1">\u5C5E\u6027 <a class="header-anchor" href="#\u5C5E\u6027" aria-hidden="true">#</a></h3><p>\u6240\u6709\u7684\u5C5E\u6027\u548C\u503C\u90FD\u662F\u5FFD\u7565\u5927\u5C0F\u5199\u7684. \u89E3\u6790\u65F6\u5B83\u4EEC\u90FD\u662F\u5C0F\u5199\u7684</p><div class="language-"><pre><code>root        \u3000\u3000  \u3000         \u8868\u793A\u662F\u6700\u9876\u5C42\u7684\u914D\u7F6E\u6587\u4EF6\uFF0C\u53D1\u73B0\u8BBE\u4E3Atrue\u65F6\uFF0C\u624D\u4F1A\u505C\u6B62\u67E5\u627E.editorconfig\u6587\u4EF6
indent_style                \u8BBE\u7F6E\u7F29\u8FDB\u98CE\u683C(tab\u662F\u786C\u7F29\u8FDB\uFF0Cspace\u4E3A\u8F6F\u7F29\u8FDB)
indent_size                 \u7528\u4E00\u4E2A\u6574\u6570\u5B9A\u4E49\u7684\u5217\u6570\u6765\u8BBE\u7F6E\u7F29\u8FDB\u7684\u5BBD\u5EA6\uFF0C\u5982\u679Cindent_style\u4E3Atab\uFF0C\u5219\u6B64\u5C5E\u6027\u9ED8\u8BA4\u4E3Atab_width
tab_width                   \u7528\u4E00\u4E2A\u6574\u6570\u6765\u8BBE\u7F6Etab\u7F29\u8FDB\u7684\u5217\u6570\u3002\u9ED8\u8BA4\u662Findent_size
end_of_line                 \u8BBE\u7F6E\u6362\u884C\u7B26\uFF0C\u503C\u4E3Alf\u3001cr\u548Ccrlf
charset                     \u8BBE\u7F6E\u7F16\u7801\uFF0C\u503C\u4E3Alatin1\u3001utf-8\u3001utf-8-bom\u3001utf-16be\u548Cutf-16le\uFF0C\u4E0D\u5EFA\u8BAE\u4F7F\u7528utf-8-bom
trim_trailing_whitespace    \u8BBE\u4E3Atrue\u8868\u793A\u4F1A\u53BB\u9664\u6362\u884C\u884C\u9996\u7684\u4EFB\u610F\u7A7A\u767D\u5B57\u7B26\u3002
insert_final_newline        \u8BBE\u4E3Atrue\u8868\u793A\u4F7F\u6587\u4EF6\u4EE5\u4E00\u4E2A\u7A7A\u767D\u884C\u7ED3\u5C3E
</code></pre></div><h2 id="\u6CE8\u91CA" tabindex="-1">\u6CE8\u91CA <a class="header-anchor" href="#\u6CE8\u91CA" aria-hidden="true">#</a></h2><p>\u4EE5\u4E95\u53F7(#)\u6216\u5206\u53F7(;)\u88AB\u7528\u4F5C\u4E8E\u6CE8\u91CA. \u6CE8\u91CA\u9700\u8981\u4E0E\u6CE8\u91CA\u7B26\u53F7\u5199\u5728\u540C\u4E00\u884C</p><h2 id="\u5B98\u7F51\u793A\u4F8B" tabindex="-1">\u5B98\u7F51\u793A\u4F8B <a class="header-anchor" href="#\u5B98\u7F51\u793A\u4F8B" aria-hidden="true">#</a></h2><div class="language-"><pre><code># top-most EditorConfig file \u8868\u793A\u662F\u6700\u9876\u5C42\u7684\u914D\u7F6E\u6587\u4EF6\uFF0C\u53D1\u73B0\u8BBE\u4E3Atrue\u65F6\uFF0C\u624D\u4F1A\u505C\u6B62\u67E5\u627E.editorconfig\u6587\u4EF6
root = true

# Unix-style newlines with a newline ending every file \u5BF9\u4E8E\u6240\u6709\u7684\u6587\u4EF6  \u59CB\u7EC8\u5728\u6587\u4EF6\u672B\u5C3E\u63D2\u5165\u4E00\u4E2A\u65B0\u884C
[*]
end_of_line = lf
insert_final_newline = true

# Matches multiple files with brace expansion notation
# Set default charset  \u5BF9\u4E8E\u6240\u6709\u7684js,py\u6587\u4EF6\uFF0C\u8BBE\u7F6E\u6587\u4EF6\u5B57\u7B26\u96C6\u4E3Autf-8
[*.{js,py}]
charset = utf-8

# 4 space indentation \u63A7\u5236py\u6587\u4EF6\u7C7B\u578B\u7684\u7F29\u8FDB\u5927\u5C0F
[*.py]
indent_style = space
indent_size = 4

# Tab indentation (no size specified) \u8BBE\u7F6E\u67D0\u4E2D\u6587\u4EF6\u7684\u7F29\u8FDB\u98CE\u683C\u4E3Atab Makefile\u672A\u6307\u660E
[Makefile]
indent_style = tab

# Indentation override for all JS under lib directory  \u8BBE\u7F6E\u5728lib\u76EE\u5F55\u4E0B\u6240\u6709JS\u7684\u7F29\u8FDB\u4E3A
[lib/**.js]
indent_style = space
indent_size = 2

# Matches the exact files either package.json or .travis.yml \u8BBE\u7F6E\u786E\u5207\u6587\u4EF6 package.json/.travis/.yml\u7684\u7F29\u8FDB\u7C7B\u578B
[{package.json,.travis.yml}]
indent_style = space
indent_size = 2
</code></pre></div><h2 id="vs-code-\u4F7F\u7528" tabindex="-1">VS Code \u4F7F\u7528 <a class="header-anchor" href="#vs-code-\u4F7F\u7528" aria-hidden="true">#</a></h2><ol><li>\u5B89\u88C5 <code>EditorConfig for VS Code</code> \u63D2\u4EF6</li><li>\u5728\u9879\u76EE\u7BA1\u7406\u5668\u4E2D\uFF0C\u4EE5\u4EFB\u4F55\u76EE\u5F55\u4E0B\uFF08\u4E00\u822C\u5728\u6839\u76EE\u5F55\u4E0B\uFF09\u53F3\u952E\uFF0C\u9009\u62E9\u6700\u540E\u4E00\u9879 <code>Generate .editorconfig</code>\uFF0C\u5C31\u4F1A\u521B\u5EFA\u914D\u7F6E\u6587\u4EF6\uFF0C\u5E76\u9884\u8BBE\u4E86\u4E00\u7EC4\u914D\u7F6E\u3002\u53EF\u4EE5\u6309\u7167\u9700\u8981\u8FDB\u884C\u66F4\u6539\u3002</li></ol><blockquote><p>\u5B89\u88C5 EditorConfig \u6269\u5C55\u63D2\u4EF6\u7684\u4F5C\u7528\u662F\u8BFB\u53D6\u521B\u5EFA\u7684 .editorconfig \u6587\u4EF6\u4E2D\u5B9A\u4E49\u7684\u89C4\u5219\uFF0C\u5E76\u8986\u76D6 user/workspace settings \u4E2D\u7684\u5BF9\u5E94\u914D\u7F6E\uFF0C\u4ECE\u8FD9\u6211\u4EEC\u4E5F\u53EF\u4EE5\u770B\u51FA vscode \u672C\u8EAB\u5C5E\u4E8E\u4E0D\u76F4\u63A5\u652F\u6301 editorconfig \u7684\u90A3\u90E8\u5206 IDE\u3002</p></blockquote><p>\u5C3D\u91CF\u8BA9 .editorconfig \u914D\u7F6E\u7684\u4EE3\u7801\u683C\u5F0F\u4E0E Prettier \u6216 ESLint \u8FD9\u4E9B\u4EE3\u7801\u683C\u5F0F\u5316\u5DE5\u5177\u914D\u7F6E\u76F8\u540C\u7684\u503C\uFF0C\u4E0D\u7136\u5BB9\u6613\u9020\u6210\u51B2\u7A81\u65E0\u6548\u3002</p><div class="language-"><pre><code># editorconfig.org
root = true

[*]
charset = utf-8
indent_size = 2
indent_style = space
insert_final_newline = true
trim_trailing_whitespace = true

# Markdown \u8BED\u8A00\u4E2D\u5C3E\u968F\u7A7A\u683C\u662F\u6709\u610F\u4E49\u7684\uFF0C\u6BD4\u5982\u884C\u5C3E\u4E24\u4E2A\u7A7A\u683C\u76F8\u5F53\u63A7\u884C, 2 trailing spaces = linebreak (&lt;br /&gt;)\uFF0C\u6240\u4EE5\u6211\u4EEC\u8981\u7279\u6B8A\u6307\u5B9A\u5FFD\u7565
# See https://daringfireball.net/projects/markdown/syntax#p
[*.md]
trim_trailing_whitespace = false
</code></pre></div><h2 id="\u53C2\u8003\u94FE\u63A5" tabindex="-1">\u53C2\u8003\u94FE\u63A5 <a class="header-anchor" href="#\u53C2\u8003\u94FE\u63A5" aria-hidden="true">#</a></h2><ul><li><a href="https://editorconfig.org/" target="_blank" rel="noopener noreferrer">EditorConfig \u5B98\u7F51</a></li><li><a href="https://blog.sesine.com/2018/12/14/editorconfig/" target="_blank" rel="noopener noreferrer">\u4F7F\u7528.editorconfig \u89C4\u8303\u7F16\u8F91\u5668\u7F16\u7801\u89C4\u8303</a></li></ul>`,27),d=[r];function l(c,s,A,f,g,h){return n(),i("div",null,d)}var v=e(o,[["render",l]]);export{u as __pageData,v as default};
