import{_ as e,c as a,o as s,a as t}from"./app.9dbb06c5.js";var r="/assets/css_standard.a6a41d37.jpg",i="/assets/css3_modules.46ef6450.jpg",o="/assets/w3c_process.eeec1e5e.jpg";const u='{"title":"CSS","description":"","frontmatter":{},"headers":[{"level":2,"title":"CSS \u5386\u53F2","slug":"css-\u5386\u53F2"},{"level":3,"title":"CSS1","slug":"css1"},{"level":3,"title":"CSS2","slug":"css2"},{"level":3,"title":"CSS3","slug":"css3"}],"relativePath":"FE-Language/CSS/index.md"}',S={},n=t('<h1 id="css" tabindex="-1">CSS <a class="header-anchor" href="#css" aria-hidden="true">#</a></h1><h2 id="css-\u5386\u53F2" tabindex="-1">CSS \u5386\u53F2 <a class="header-anchor" href="#css-\u5386\u53F2" aria-hidden="true">#</a></h2><p>CSS \u662F\u5F00\u653E\u7F51\u7EDC\u7684\u6838\u5FC3\u8BED\u8A00\u4E4B\u4E00\uFF0C\u7531 W3C \u89C4\u8303 \u5B9E\u73B0\u8DE8\u6D4F\u89C8\u5668\u7684\u6807\u51C6\u5316\uFF0C\u4F46CSS\u5E76\u4E0D\u662F\u6700\u521Dwww\u7CFB\u7EDF\u89C4\u8303\u6587\u6863\u4E4B\u4E00\u3002</p><p>HTML \u548C CSS \u662F\u90A3\u4E48\u5BC6\u4E0D\u53EF\u5206\uFF0C\u4EE5\u81F3\u4E8E\u4F60\u53EF\u80FD\u4F1A\u89C9\u5F97\u5B83\u4EEC\u662F\u4E00\u8D77\u51FA\u73B0\u7684\u3002\u5B9E\u9645\u4E0A\uFF0C\u81EA 1989 \u5E74 Tim Berners Lie \u53D1\u660E\u4E92\u8054\u7F51\u540E\u7684\u591A\u5E74\u4E2D\uFF0C\u8FD9\u4E2A\u4E16\u754C\u4E0A\u90FD\u4E0D\u5B58\u5728\u4E00\u4E2A\u540D\u4E3A CSS \u7684\u4E8B\u7269\uFF0CWeb \u7684\u539F\u59CB\u7248\u672C\u6839\u672C\u5C31\u6CA1\u6709\u63D0\u4F9B\u8FD9\u79CD\u88C5\u9970\u7F51\u9875\u7684\u65B9\u6CD5\u3002</p><p>HTML \u89C4\u8303\u867D\u7136\u89C4\u5B9A\u4E86\u7F51\u9875\u4E2D\u7684\u6807\u9898\u3001\u6BB5\u843D\u5E94\u8BE5\u4F7F\u7528\u7684\u6807\u7B7E\uFF0C\u4F46\u662F\u6CA1\u6709\u6D89\u53CA\u8FD9\u4E9B\u5185\u5BB9\u5E94\u8BE5\u4EE5\u4F55\u79CD\u6837\u5F0F(\u6BD4\u5982\u5927\u5C0F\u3001\u4F4D\u7F6E\u3001\u95F4\u8DDD\u3001\u7F29\u8FDB\u7B49\u5C5E\u6027)\u5448\u73B0\u5728\u6D4F\u89C8\u5668\u4E2D\u3002 \u4E0D\u8FC7\uFF0C\u5728\u968F\u540E\u4EC5\u77ED\u77ED10\u5E74\u540E\uFF0CCSS\u5C31\u88AB\u4E00\u4E2A\u73B0\u4EE3\u7684 Web \u793E\u533A\u5168\u9762\u91C7\u7528\uFF0C\u8FD9\u671F\u95F4\u7684\u53D1\u751F\u4E86\u4E00\u7CFB\u5217\u6709\u8DA3\u7684\u6545\u4E8B\uFF0C\u6709\u5174\u8DA3\u7684\u53EF\u4EE5\u770B\u4E00\u4E0B <a href="https://thehistoryoftheweb.com/look-back-history-css/" target="_blank" rel="noopener noreferrer">A Look Back at the History of CSS</a>\u3002</p><h3 id="css1" tabindex="-1">CSS1 <a class="header-anchor" href="#css1" aria-hidden="true">#</a></h3><p>1994\u5E74\uFF0CH\xE5kon Wium Lie (\u54C8\u80AF\xB7\u7EF4\u59C6\xB7\u83B1) \u548C Bert Bos (\u4F2F\u7279\xB7\u6CE2\u65AF) \u5408\u4F5C\u8BBE\u8BA1CSS\u3002\u4ED6\u4EEC\u57281994\u5E74\u9996\u6B21\u5728\u829D\u52A0\u54E5\u7684\u4E00\u6B21\u4F1A\u8BAE\u4E0A\u7B2C\u4E00\u6B21\u5C55\u793A\u4E86CSS\u7684\u63D0\u8BAE\u3002</p><p>1996\u5E7412\u6708\uFF0C\u53D1\u8868\u7684 CSS1\uFF0C\u7B2C\u4E00\u7248\u4E3B\u8981\u89C4\u5B9A\u4E86\u9009\u62E9\u5668\u3001\u6837\u5F0F\u5C5E\u6027\u3001\u4F2A\u7C7B \u3001\u5BF9\u8C61\u51E0\u4E2A\u5927\u7684\u90E8\u5206\u3002</p><h3 id="css2" tabindex="-1">CSS2 <a class="header-anchor" href="#css2" aria-hidden="true">#</a></h3><p>CSS2 \u5728 1998 \u5E74 5\u6708 \u7531 W3C \u53D1\u5E03\uFF0CCSS2 \u89C4\u8303\u662F\u57FA\u4E8E CSS1 \u8BBE\u8BA1\u7684\uFF0C\u6269\u5145\u548C\u6539\u8FDB\u4E86\u5F88\u591A\u66F4\u52A0\u5F3A\u5927\u7684\u5C5E\u6027\u3002\u5305\u62EC\u9009\u62E9\u5668\u3001\u4F4D\u7F6E\u6A21\u578B\u3001\u5E03\u5C40\u3001\u8868\u683C\u6837\u5F0F\u3001\u5A92\u4F53\u7C7B\u578B\u3001\u4F2A\u7C7B\u3001\u5149\u6807\u6837\u5F0F\u3002</p><p>Cascading Style Sheets Level 2 Revision 1\uFF0C\u901A\u5E38\u88AB\u79F0\u4E3A\u201C CSS 2.1\u201D \uFF0C\u4FEE\u590D\u4E86 CSS 2\u4E2D\u7684\u9519\u8BEF\uFF0C\u5220\u9664\u4E86\u652F\u6301\u4E0D\u826F\u6216\u4E0D\u80FD\u5B8C\u5168\u4E92\u64CD\u4F5C\u7684\u7279\u6027\uFF0C\u5E76\u4E3A\u89C4\u8303\u589E\u52A0\u4E86\u5DF2\u7ECF\u5B9E\u73B0\u7684\u6D4F\u89C8\u5668\u6269\u5C55\u3002\u4E3A\u4E86\u9075\u5B88 W3C \u6807\u51C6\u5316\u6280\u672F\u89C4\u8303\u7684\u8FC7\u7A0B\uFF0CCSS 2.1 \u5728 Working Draft (WD) \u72B6\u6001\u548C Candidate Recommendation (CP) \u72B6\u6001\u4E4B\u95F4\u6765\u56DE\u4E86\u5F88\u591A\u5E74\u3002</p><p>CSS 2.1\u4E8E 2004 \u5E74 2 \u6708 25 \u65E5\u9996\u6B21\u6210\u4E3A Candidate Recommendation (CR)\u6807\u51C6\uFF0C\u4F46\u5728 2005 \u5E74 6 \u6708 13 \u65E5\u53C8\u56DE\u5230 Working Draft (WD) \u4E2D\u8FDB\u884C\u8FDB\u4E00\u6B65\u5BA1\u67E5\u3002\u5B83\u4E8E 2007 \u5E74 7 \u6708 19 \u65E5\u56DE\u5230 Candidate Recommendation (CP) \u6807\u51C6\uFF0C\u7136\u540E\u5728 2009 \u5E74\u66F4\u65B0\u4E86\u4E24\u6B21\u3002\u7136\u800C\uFF0C\u7531\u4E8E\u4F5C\u51FA\u4E86\u4FEE\u6539\u548C\u6F84\u6E05\uFF0C\u5B83\u518D\u6B21\u56DE\u5230\u4E862010\u5E7412\u67087\u65E5\u7684 Last Call Working Draft \u3002</p><h3 id="css3" tabindex="-1">CSS3 <a class="header-anchor" href="#css3" aria-hidden="true">#</a></h3><p>CSS3 \u662F\u5C42\u53E0\u6837\u5F0F\u8868\uFF08Cascading Style Sheets\uFF09\u8BED\u8A00\u7684\u6700\u65B0\u7248\u672C\uFF0C\u65E8\u5728\u6269\u5C55CSS2.1\u3002</p><p>CSS Level 2 \u7ECF\u5386\u4E86 9 \u5E74\u7684\u65F6\u95F4\uFF08\u4ECE 2002 \u5E74 8 \u6708\u5230 2011 \u5E74 6 \u6708\uFF09\u624D\u8FBE\u5230 Recommendation\uFF08\u63A8\u8350\uFF09 \u72B6\u6001\uFF0C\u4E3B\u8981\u539F\u56E0\u662F\u88AB\u4E00\u4E9B\u6B21\u8981\u7279\u6027\u62D6\u4E86\u540E\u817F\u3002\u4E3A\u4E86\u52A0\u5FEB\u90A3\u4E9B\u5DF2\u7ECF\u786E\u8BA4\u6CA1\u6709\u95EE\u9898\u7684\u7279\u6027\u7684\u6807\u51C6\u5316\u901F\u5EA6\uFF0CW3C \u7684 CSS Working Group \u4F5C\u51FA\u4E86\u4E00\u9879\u88AB\u79F0\u4E3A Beijing doctrine \u7684\u51B3\u5B9A\uFF0C\u5C06 CSS \u5212\u5206\u4E3A\u8BB8\u591A\u5C0F\u7EC4\u4EF6\uFF0C\u79F0\u4E4B\u4E3A\u6A21\u5757\u3002\u8FD9\u4E9B\u6A21\u5757\u5F7C\u6B64\u72EC\u7ACB\uFF0C\u6309\u7167\u5404\u81EA\u7684\u8FDB\u5EA6\u6765\u8FDB\u884C\u6807\u51C6\u5316\u3002\u5176\u4E2D\u4E00\u4E9B\u5DF2\u7ECF\u662F W3C Recommendation \u72B6\u6001\uFF0C\u4E5F\u6709\u4E00\u4E9B\u4ECD\u662F Early Working Drafts\uFF08\u65E9\u671F\u5DE5\u4F5C\u8349\u6848\uFF09\u3002\u5F53\u65B0\u7684\u9700\u6C42\u88AB\u80AF\u5B9A\u540E\uFF0C \u65B0\u7684\u6A21\u5757\u4E5F\u4F1A\u540C\u6837\u5730\u6DFB\u52A0\u8FDB\u6765\u3002</p><p>\u6240\u4EE5\u4ECE\u7248\u672C\u6765\u8BB2\uFF0C\u5DF2\u7ECF\u4E0D\u5B58\u5728CSS4\u4E86\uFF0CW3C\u4F1A\u6309\u6A21\u5757\u7EA7\u522B\u6570\uFF08level\uFF09\u53D1\u5E03\u6807\u51C6\uFF0C\u5728\u6BCF\u4E2A\u65F6\u95F4\u70B9\u4E0A\u4E3A CSS \u6807\u51C6\u5B9A\u4E49\u4E00\u4E2A snapshots\uFF08\u5FEB\u7167\uFF09\uFF0C\u5217\u51FA\u5404\u4E2A\u6210\u719F\u7684\u6A21\u5757\u7684\u7EA7\u522B\u3002W3C \u4F1A\u5B9A\u671F\u6309\u5E74\u4EFD\u53D1\u5E03\u8FD9\u4E9B snapshots\uFF0C\u5982 2007, 2010, 2015 \u6216 2017\u3002 <img src="'+r+'" alt=""></p><p>\u6709\u4E00\u5F20\u56FE\u53EF\u4EE5\u66F4\u52A0\u76F4\u89C2\u7684\u8868\u793A\u5F53\u524D CSS3 Modules \u7684\u5206\u7C7B\u548C\u72B6\u6001\uFF1A<br><img src="'+i+'" width="500" height="500"></p><blockquote><p>\u8865\u5145\uFF1AW3C \u89C4\u8303\u5236\u5B9A\u6D41\u7A0B</p></blockquote><p>\u6309\u7167 W3C \u7684 Process Document\uFF0C\u4E00\u4E2A\u63A8\u8350\u6807\u51C6\u7684\u53D1\u5C55\u9700\u8981\u901A\u8FC7\u4E0D\u540C\u7684\u9636\u6BB5\u3002</p><p>\u5176\u4E2D\u51E0\u4E2A\u4E3B\u8981\u7684\u9636\u6BB5\u5206\u522B\u4E3A\uFF1A</p><ul><li>WD \u5DE5\u4F5C\u8349\u6848\uFF08Working draft\uFF09</li><li>CR \u5019\u9009\u63A8\u8350\u6807\u51C6\uFF08Candidate recommendation\uFF09</li><li>PR \u63D0\u6848\u63A8\u8350\u6807\u51C6\uFF08recommendation\uFF09</li><li>REC W3C\u63A8\u8350\u6807\u51C6\uFF08W3C recommendation\uFF09</li></ul><p><img src="'+o+'" alt=""></p>',22),c=[n];function d(C,l,p,h,_,m){return s(),a("div",null,c)}var W=e(S,[["render",d]]);export{u as __pageData,W as default};
