import{_ as e,c as r,o as t,a}from"./app.f1e974b0.js";const u='{"title":"HTML","description":"","frontmatter":{},"headers":[{"level":2,"title":"WWW \u4E07\u7EF4\u7F51\u7684\u53D1\u660E","slug":"www-\u4E07\u7EF4\u7F51\u7684\u53D1\u660E"},{"level":3,"title":"W3C","slug":"w3c"},{"level":3,"title":"WHATWG","slug":"whatwg"}],"relativePath":"FE-Language/HTML/0-introduce.md"}',l={},i=a('<h1 id="html" tabindex="-1">HTML <a class="header-anchor" href="#html" aria-hidden="true">#</a></h1><ul><li>HTML \u5168\u79F0\uFF1AHyperText Markup Language\uFF08\u8D85\u6587\u672C\u6807\u8BB0\u8BED\u8A00\uFF09\u3002</li><li>HTML \u662F\u4E00\u79CD\u7528\u6765\u544A\u77E5\u6D4F\u89C8\u5668\u5982\u4F55\u7EC4\u7EC7\u9875\u9762\u7684<strong>\u6807\u8BB0\u8BED\u8A00</strong>\uFF0C\u800C\u4E0D\u662F\u4E00\u95E8\u7F16\u7A0B\u8BED\u8A00\u3002</li><li>HTML \u7531\u4E00\u7CFB\u5217\u7684\u5143\u7D20\uFF08elements\uFF09\u7EC4\u6210\uFF0C\u8FD9\u4E9B\u5143\u7D20\u53EF\u4EE5\u7528\u6765\u5305\u56F4\u4E0D\u540C\u90E8\u5206\u7684\u5185\u5BB9\uFF0C\u4F7F\u5176\u4EE5\u67D0\u79CD\u65B9\u5F0F\u5728\u9875\u9762\u4E0A\u5448\u73B0\u3002</li></ul><blockquote><p>1989\u5E74\uFF0C\u8482\u59C6\xB7\u4F2F\u7EB3\u65AF-\u674E\uFF08Tim Berners-Lee\uFF09\u5728 CERN \u63D0\u51FA\u4E07\u7EF4\u7F51\u96CF\u5F62\u62A5\u544A\u7684\u65F6\u5019\uFF0CHTML\u662F\u4F5C\u4E3A\u4E07\u7EF4\u7F51\u7CFB\u7EDF\u56DB\u90E8\u5206\u4E4B\u4E00\u7684\u5B58\u5728\u3002\u6709\u53C2\u7167\u5F53\u65F6\u5DF2\u5B58\u5728\u7684SGML\u6807\u8BB0\u8BED\u8A00\u3002</p></blockquote><p><a href="https://html.spec.whatwg.org/multipage/indices.html" target="_blank" rel="noopener noreferrer">W3C HTML Standard</a></p><h2 id="www-\u4E07\u7EF4\u7F51\u7684\u53D1\u660E" tabindex="-1">WWW \u4E07\u7EF4\u7F51\u7684\u53D1\u660E <a class="header-anchor" href="#www-\u4E07\u7EF4\u7F51\u7684\u53D1\u660E" aria-hidden="true">#</a></h2><p>1989\u5E74\uFF0C \u5F53\u65F6\u5728 CERN \u5DE5\u4F5C\u7684 Tim Berners-Lee \uFF08\u8482\u59C6\xB7\u4F2F\u7EB3\u65AF-\u674E\uFF09\u535A\u58EB\u5199\u4E86\u4E00\u4EFD\u5173\u4E8E\u5EFA\u7ACB\u4E00\u4E2A\u901A\u8FC7\u7F51\u7EDC\u4F20\u8F93\u8D85\u6587\u672C\u7CFB\u7EDF\u7684\u62A5\u544A\uFF0C\u62A5\u544A\u4E2D\u9610\u8FF0\u7684\u8FD9\u4E2A\u7CFB\u7EDF\u5305\u62EC\u56DB\u4E2A\u90E8\u5206\uFF1A</p><ul><li>\u4E00\u4E2A\u7528\u6765\u8868\u793A\u8D85\u6587\u672C\u6587\u6863\u7684\u6587\u672C\u683C\u5F0F\uFF0C\u8D85\u6587\u672C\u6807\u8BB0\u8BED\u8A00\uFF08HTML\uFF09\u3002</li><li>\u4E00\u4E2A\u7528\u6765\u4EA4\u6362\u8D85\u6587\u672C\u6587\u6863\u7684\u7B80\u5355\u534F\u8BAE\uFF0C\u8D85\u6587\u672C\u4F20\u8F93\u534F\u8BAE\uFF08HTTP\uFF09\u3002</li><li>\u4E00\u4E2A\u663E\u793A\uFF08\u4EE5\u53CA\u7F16\u8F91\uFF09\u8D85\u6587\u672C\u6587\u6863\u7684\u5BA2\u6237\u7AEF\uFF0C\u5373\u7F51\u7EDC\u6D4F\u89C8\u5668\uFF08Browser\uFF09\u3002</li><li>\u4E00\u4E2A\u7528\u4E8E\u63D0\u4F9B\u53EF\u8BBF\u95EE\u7684\u6587\u6863\u7684\u670D\u52A1\u5668\uFF08Server\uFF09\uFF0C\u5373 httpd \u7684\u524D\u8EAB\u3002</li></ul><p>\u8FD9\u56DB\u4E2A\u90E8\u5206\u5B8C\u6210\u4E8E1990\u5E74\u5E95\uFF0C1991\u5E748\u670816\u65E5\uFF0CTim Berners-Lee \u5728\u516C\u5F00\u7684\u8D85\u6587\u672C\u65B0\u95FB\u7EC4\u4E0A\u53D1\u8868\u7684\u6587\u7AE0\u88AB\u89C6\u4E3A\u662F\u4E07\u7EF4\u7F51\u516C\u5171\u9879\u76EE\u7684\u5F00\u59CB\u3002</p><blockquote><p>\u8FD9\u4E2A\u7CFB\u7EDF\u5728\u6700\u521D\u7ACB\u9879\u65F6\u88AB\u547D\u540D\u4E3A Mesh\uFF0C\u4F46\u5728\u968F\u540E\u76841990\u5E74\u9879\u76EE\u5B9E\u65BD\u671F\u95F4\u88AB\u66F4\u540D\u4E3A\u4E07\u7EF4\u7F51\uFF08World Wide Web\uFF09\u3002</p></blockquote><blockquote><p>httpd \u4E00\u4E2A\u72EC\u7ACB\u8FD0\u884C\u7684\u540E\u53F0\u8FDB\u7A0B\uFF0C\u5B83\u4F1A\u5EFA\u7ACB\u4E00\u4E2A\u5904\u7406\u8BF7\u6C42\u7684\u5B50\u8FDB\u7A0B\u6216\u7EBF\u7A0B\u6C60\u3002\u6700\u65E9\u4F5C\u4E3A Apache \u670D\u52A1\u5668\u7684\u4E3B\u7A0B\u5E8F\u3002</p></blockquote><h3 id="w3c" tabindex="-1">W3C <a class="header-anchor" href="#w3c" aria-hidden="true">#</a></h3><p>W3C \u4E5F\u5C31\u662F\u4E07\u7EF4\u7F51\u8054\u76DF\uFF08World Wide Web Consortium\uFF09\uFF0C\u53C8\u79F0 W3C \u7406\u4E8B\u4F1A\uFF0C\u662F\u4E07\u7EF4\u7F51\u7684\u4E3B\u8981\u56FD\u9645\u6807\u51C6\u7EC4\u7EC7\uFF0C\u662F\u4E00\u4E2A\u534A\u81EA\u6CBB\u975E\u653F\u5E9C\u7EC4\u7EC7\uFF08quasi-autonomous non-governmental organisation\uFF09\u3002</p><ul><li>\u521B\u7ACB\uFF1A\u7531 \u8482\u59C6\xB7\u4F2F\u7EB3\u65AF-\u674E\uFF08Tim Berners-Lee\uFF09\u4E8E 1994\u5E7410\u6708 \u4E8E\u9EBB\u7701\u7406\u5DE5\u5B66\u9662 MIT \u8BA1\u7B97\u673A\u79D1\u5B66\u4E0E\u4EBA\u5DE5\u667A\u80FD\u5B9E\u9A8C\u5BA4\uFF08MIT\uFF0FLCS\uFF09\u521B\u7ACB\u3002</li><li>\u804C\u8D23\uFF1AW3C \u5236\u5B9A\u4E86\u4E00\u7CFB\u5217\u6807\u51C6\u5E76\u7763\u4FC3\u7F51\u7EDC\u5E94\u7528\u5F00\u53D1\u8005\uFF08\u5982\u6D4F\u89C8\u5668\u5382\u5546\uFF09\u548C\u5185\u5BB9\u63D0\u4F9B\u8005\uFF08\u5F00\u53D1\u8005\uFF09\u9075\u5FAA\u8FD9\u4E9B\u6807\u51C6\u3002\u6807\u51C6\u7684\u5185\u5BB9\u5305\u62EC\u4F7F\u7528\u8BED\u8A00\u7684\u89C4\u8303\u3001\u5F00\u53D1\u4E2D\u4F7F\u7528\u7684\u89C4\u5219\u548C\u89E3\u91CA\u5F15\u64CE\u7684\u884C\u4E3A\u7B49\u7B49\u3002W3C\u5236\u5B9A\u4E86\u5305\u62EC HTML\u3001DOM\u3001SVG\u3001XML\u548C CSS \u7B49\u7684\u4F17\u591A\u5F71\u54CD\u6DF1\u8FDC\u7684\u6807\u51C6\u89C4\u8303\u3002</li></ul><h3 id="whatwg" tabindex="-1">WHATWG <a class="header-anchor" href="#whatwg" aria-hidden="true">#</a></h3><p>WHATWG \u5168\u79F0 \u7F51\u9875\u8D85\u6587\u5B57\u5E94\u7528\u6280\u672F\u5DE5\u4F5C\u5C0F\u7EC4\uFF08Web Hypertext Application Technology Working Group\uFF09\u3002</p><ul><li>\u521B\u7ACB\uFF1A\u7531Web \u6D4F\u89C8\u5668\u751F\u4EA7\u5382\u5546\uFF08\u4E3B\u8981\u662FOpera\u3001Mozilla\u57FA\u91D1\u4F1A\u548C\u82F9\u679C\uFF0C\u540E\u671FGoogle\u4E5F\u52A0\u5165\uFF09\u548C\u4E00\u4E9B\u76F8\u5173\u56E2\u4F53\u5F62\u6210\u7684\u4E00\u4E2A\u7684\u534F\u4F5C\u7EC4\u7EC7\u3002</li><li>\u804C\u8D23\uFF1AWHATWG \u7684\u6807\u5FD7\u6027\u89C4\u8303\u662F HTML5\u89C4\u8303\u7684\u5236\u5B9A\u3002</li><li>\u7F18\u7531\uFF1AW3C\u5BF9HTML\u7684\u53D1\u5C55\u65B9\u5411\u51FA\u73B0\u8C03\u6574\uFF0C\u5927\u529B\u63A8\u52A8XHMTL\u7684\u53D1\u5C55\uFF0C\u4E0E\u6D4F\u89C8\u5668\u5382\u5546\u7684\u65B9\u5411\u4E0D\u540C\uFF0C\u6240\u4EE5\u53E6\u8D77\u7089\u7076\u5F62\u6210\u4E86WHATWG\u7EC4\u7EC7\uFF0C\u63A8\u51FAHTML5\u89C4\u8303\u3002\u6700\u7EC8W3C\u56DE\u8FC7\u6765\u627F\u8BA4HTML5\u4F5C\u4E3AHTML\u7684\u6700\u65B0\u89C4\u8303\uFF0CW3C\u5C06\u4E0EWHATWG\u4E00\u8D77\u5408\u4F5C\u5236\u5B9A\u6700\u65B0HTML\u548CDOM\u89C4\u8303\u3002</li></ul><p><a href="http://www.sohu.com/a/317425124_394375" target="_blank" rel="noopener noreferrer">W3C\u5C06\u4E0EWHATWG\u5408\u4F5C\u5236\u5B9A\u6700\u65B0HTML\u548CDOM\u89C4\u8303 </a><br><a href="http://news.mydrivers.com/1/239/239149.htm" target="_blank" rel="noopener noreferrer">HTML5\u7684\u653F\u6CBB\u6597\u4E89\uFF1A\u8FD8\u8981\u95F9\u5341\u5E74\uFF1F</a></p>',17),o=[i];function n(h,s,p,c,d,T){return t(),r("div",null,o)}var _=e(l,[["render",n]]);export{u as __pageData,_ as default};
