import{_ as n,c as e,o as i,a as s}from"./app.fa011131.js";const x='{"title":"bash","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u64CD\u4F5C\u7CFB\u7EDFUnix\u3001Linus\u3001Linux\u3001Windows\u3001Mac OS\u3001Ubuntu","slug":"\u64CD\u4F5C\u7CFB\u7EDFunix\u3001linus\u3001linux\u3001windows\u3001mac-os\u3001ubuntu"},{"level":2,"title":"Linux \u4E2D\u7684 shell\u3001bash\u3001terminal\u548Ckernel\u4E4B\u95F4\u7684\u5173\u7CFB","slug":"linux-\u4E2D\u7684-shell\u3001bash\u3001terminal\u548Ckernel\u4E4B\u95F4\u7684\u5173\u7CFB"},{"level":2,"title":"git\u3001git bash\u548Cgit shell\u6709\u4EC0\u4E48\u533A\u522B\uFF1F","slug":"git\u3001git-bash\u548Cgit-shell\u6709\u4EC0\u4E48\u533A\u522B\uFF1F"},{"level":2,"title":"#!/bin/bash \u548C #!/usr/bin/bash \u548C #!/usr/bin/env bash \u548C #!/usr/bin/env node \u7684\u6BD4\u8F83","slug":"bin-bash-\u548C-usr-bin-bash-\u548C-usr-bin-env-bash-\u548C-usr-bin-env-node-\u7684\u6BD4\u8F83"},{"level":2,"title":"package.json \u4E2D\u7684 bin \u5B57\u6BB5","slug":"package-json-\u4E2D\u7684-bin-\u5B57\u6BB5"}],"relativePath":"Server/bash/index.md"}',l={},a=s(`<h1 id="bash" tabindex="-1">bash <a class="header-anchor" href="#bash" aria-hidden="true">#</a></h1><p><a href="https://wangdoc.com/bash/" target="_blank" rel="noopener noreferrer">bash\u6559\u7A0B</a> --- \u7F51\u9053\u6587\u6863\uFF0C\u962E\u4E00\u5CF0\u5927\u795E\u7EF4\u62A4\u7684\u5F88\u591A\u5F00\u6E90\u6559\u7A0B <a href="https://wiki.ubuntu.org.cn/Shell%E7%BC%96%E7%A8%8B%E5%9F%BA%E7%A1%80" target="_blank" rel="noopener noreferrer">Shell\u7F16\u7A0B\u57FA\u7840</a>----\u66F4\u4E3A\u7B80\u6D01\u4E00\u70B9</p><h2 id="\u64CD\u4F5C\u7CFB\u7EDFunix\u3001linus\u3001linux\u3001windows\u3001mac-os\u3001ubuntu" tabindex="-1">\u64CD\u4F5C\u7CFB\u7EDFUnix\u3001Linus\u3001Linux\u3001Windows\u3001Mac OS\u3001Ubuntu <a class="header-anchor" href="#\u64CD\u4F5C\u7CFB\u7EDFunix\u3001linus\u3001linux\u3001windows\u3001mac-os\u3001ubuntu" aria-hidden="true">#</a></h2><p>Unix[\u02C8yo\u035Eoniks]: Linux[\u02C8lin\u0259ks]: Linux \u672C\u8EAB\u53EA\u662F\u4E00\u4E2A\u5185\u6838\u7A0B\u5E8F\uFF0C\u5B83\u4E3A\u7A0B\u5E8F\u5458\u5B9A\u4E49\u4E86\u64CD\u4F5C\u7CFB\u7EDF\u7684\u57FA\u672C\u5143\u7D20\uFF0C\u4F46\u6700\u7EC8\u7528\u6237\u65E0\u6CD5\u76F4\u63A5\u64CD\u4F5C\u5B83\u4E5F\u65E0\u6CD5\u4F7F\u7528\u5B83\u3002Linux \u672C\u8EAB\u4ECE\u6765\u5C31\u6CA1\u6709\u5B9A\u4E49\u5B83\u8BE5\u662F\u4EC0\u4E48\u56FE\u5F62\u7CFB\u7EDF\uFF0C\u4E0E\u7528\u6237\u8BE5\u662F\u4EC0\u4E48\u64CD\u4F5C\uFF0C\u751A\u81F3\u4E5F\u4ECE\u6765\u6CA1\u6709\u5B9A\u4E49\u5B83\u8BE5\u6709\u4EC0\u4E48\u547D\u4EE4\u884C\u3002\u738B\u57A0\u66FE\u7ECF\u8BF4\u8FC7\uFF1ALinux \u53EF\u4EE5\u662F\u8FD9\u6837\uFF0CLinux \u53EF\u4EE5\u662F\u90A3\u6837\uFF0CLinux \u53EF\u4EE5\u662F\u4EFB\u4F55\u6837\u5B50\u3002Linux \u662F\u4E00\u4E2A\u76F8\u5BF9\u81EA\u7531\u7684\u4E16\u754C\uFF0C\u6240\u4EE5\u56F4\u7ED5 Linux \u5185\u6838\u4F60\u53EF\u4EE5\u642D\u5EFA\u51FA\u5404\u79CD\u5404\u6837\u7684\u4E0A\u5C42\u7CFB\u7EDF\u3002\u8FD9\u6837\u642D\u5EFA\u8D77\u6765\u7684\u7CFB\u7EDF\uFF0C\u79F0\u4E3A\u300C\u53D1\u884C\u7248\u300D\u3002</p><ul><li>Android \u662F\u76EE\u524D\u5F53\u4E0B\u7528\u6237\u6700\u591A\u7684 Linux \u53D1\u884C\u7248\uFF0CUbuntu \u662F\u57FA\u4E8E Linux \u5F00\u53D1\u7684\u56FE\u5F62\u5316\u754C\u9762\u7CFB\u7EDF\u7684\u53D1\u884C\u7248\uFF0C\u7C7B\u4F3C Windows \u7684\u56FE\u5F62\u5316\u64CD\u4F5C\u754C\u9762\u3002</li><li>Shell \u4F5C\u4E3A\u58F3\uFF0C\u662F\u76F8\u5BF9 Linux \u4F5C\u4E3A\u5185\u6838\u6765\u8BF4\u7684\uFF0Cshell \u63D0\u4F9B\u4E86\u7528\u6237\u64CD\u4F5C Linux \u5185\u6838\u7684\u547D\u4EE4\u884C\u754C\u9762\u5DE5\u5177\uFF0C\u8FD0\u884C\u5728 shell \u4E0A\u7684 Linux \u547D\u4EE4\u89E3\u91CA\u7A0B\u5E8F\u4E5F\u6709\u591A\u79CD\uFF0Cbash\u662F\u5176\u4E2D\u4E00\u79CD\u4E14\u4F5C\u4E3ALinux\u53D1\u884C\u7248\u7684\u9ED8\u8BA4\u89E3\u91CA\u5668\u3002</li><li>git-bash \u662F\u8FD0\u884C\u5728 windows \u7CFB\u7EDF\u4E0A\u6A21\u62DF shell \u529F\u80FD\u7684\u547D\u4EE4\u884C\u7EC8\u7AEF\u3002</li></ul><p>Linux\u548CMac OS\u7684\u533A\u522B\uFF1A\u90FD\u662F\u57FA\u4E8E\u7C7BUnix\u7684\uFF0C\u4E0D\u8FC7Mac OS X\u5C5E\u4E8EUnix\u7684\u76F4\u63A5\u884D\u751F\u4EA7\u54C1</p><ul><li>Linux \u548C macOS \u90FD\u662F\u8D77\u6E90\u4E8E UNIX</li><li>Linux \u662F\u5B8F\u5185\u6838\uFF0CmacOS \u7684\u5185\u6838 Darwin \u662F\u6DF7\u5408\u5185\u6838</li><li>Linux \u8D77\u6E90\u4E8E Linus\uFF0CDarwin \u662F\u57FA\u4E8E Mach \u548C BSD \u4FEE\u6539\u800C\u6765</li><li>Linux \u53EA\u662F \u7C7BUNIX\uFF0C\u800C macOS \u662F\u901A\u8FC7\u4E86 Single UNIX Specification \u7684\u6B63\u7EDF UNIX</li></ul><p>Windows \u548C Linux\u7684\u533A\u522B linux\u4E3B\u8981\u5E94\u7528\u4E8E\u4F01\u4E1A\u670D\u52A1\u5668\u64CD\u4F5C\u7CFB\u7EDF \u5168\u7403\u5927\u90E8\u5206\u7684\u4F01\u4E1A\u670D\u52A1\u5668\u90FD\u662Flinux\u7CFB\u7EDF\uFF08\u5927\u7EA6\u536097%\uFF09 \u5168\u7403\u9886\u5148\u8DD1\u7684\u6700\u5FEB\u7684\u670D\u52A1\u5668\u4E5F\u662Flinux\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u53EF\u4EE5\u8BF4\u5728\u4F01\u4E1A\u670D\u52A1\u5668\u65B9\u9762windows\u4E0D\u582A\u4E00\u51FB\uFF1B\u5982\u679C\u529E\u516C\u5A31\u4E50\u7684\u8BDD\uFF0C\u5F53\u7136\u662Fwindows\u7684\u8001\u5927\u4E86\uFF0Clinux\u5728\u684C\u9762\u7CFB\u7EDF\u4E0A\u4E5F\u82B1\u8D39\u4E86\u4E0D\u5C11\u52AA\u529B\uFF0C\u6BD4\u5982Ubuntu Fedora \u4ED6\u4EEC\u7684\u684C\u9762\u7CFB\u7EDF\u8981\u6BD4windows\u505A\u7684\u534E\u4E3D\u5F88\u591A\uFF0C\u5C31\u662F\u4E0D\u80FD\u5E7F\u6CDB\u5E94\u7528\uFF1B</p><p>Windows\uFF1A\u666E\u901A\u7528\u6237\u57FA\u672C\u90FD\u662F\u7EAF\u56FE\u5F62\u754C\u9762\u4E0B\u64CD\u4F5C\u4F7F\u7528\uFF0C\u4F9D\u9760\u9F20\u6807\u548C\u952E\u76D8\u5B8C\u6210\u4E00\u5207\u64CD\u4F5C\uFF0C\u7528\u6237\u4E0A\u624B\u5BB9\u6613\uFF0C\u5165\u95E8\u7B80\u5355\uFF1BLinux\uFF1A\u517C\u5177\u56FE\u5F62\u754C\u9762\u64CD\u4F5C\uFF08\u9700\u8981\u4F7F\u7528\u5E26\u6709\u684C\u9762\u73AF\u5883\u7684\u53D1\u884C\u7248\uFF09\u548C\u5B8C\u5168\u7684\u547D\u4EE4\u884C\u64CD\u4F5C\uFF0C\u53EF\u4EE5\u53EA\u7528\u952E\u76D8\u5B8C\u6210\u4E00\u5207\u64CD\u4F5C</p><p>\u53C2\u8003\u94FE\u63A5\uFF1A</p><ul><li><a href="https://blog.csdn.net/zhanghow/article/details/53542397" target="_blank" rel="noopener noreferrer">\u64CD\u4F5C\u7CFB\u7EDFUnix\u3001Windows\u3001Mac OS\u3001Linux\u7684\u6545\u4E8B \u79D1\u666E\u7BC7</a>----\u8BE6\u7EC6\u8BB2\u89E3\u4E86\u5404\u4E2A\u64CD\u4F5C\u7CFB\u7EDF\u7684\u53D1\u5C55\u5386\u7A0B</li><li><a href="https://www.zhihu.com/question/19653283" target="_blank" rel="noopener noreferrer">Linux \u548C macOS \u7684\u5177\u4F53\u5DEE\u5F02\u6709\u54EA\u4E9B\uFF1F</a> --- \u7B2C\u4E8C\u4E2A\u56DE\u7B54</li><li><a href="https://upload.wikimedia.org/wikipedia/commons/7/77/Unix_history-simple.svg" target="_blank" rel="noopener noreferrer">\u64CD\u4F5C\u7CFB\u7EDF\u5185\u6838\u53D1\u5C55\u65F6\u95F4\u56FE\u8C31</a></li></ul><h2 id="linux-\u4E2D\u7684-shell\u3001bash\u3001terminal\u548Ckernel\u4E4B\u95F4\u7684\u5173\u7CFB" tabindex="-1">Linux \u4E2D\u7684 shell\u3001bash\u3001terminal\u548Ckernel\u4E4B\u95F4\u7684\u5173\u7CFB <a class="header-anchor" href="#linux-\u4E2D\u7684-shell\u3001bash\u3001terminal\u548Ckernel\u4E4B\u95F4\u7684\u5173\u7CFB" aria-hidden="true">#</a></h2><ul><li>kernel\uFF1A\u4E2D\u6587\u662F\u201C\u64CD\u4F5C\u7CFB\u7EDF\u6838\u5FC3\u201D\uFF0C\u4E3B\u8981\u7528\u4E8E\u7BA1\u7406\u786C\u4EF6\u548C\u63D0\u4F9B\u76F8\u5173\u7684\u80FD\u529B\u5B9E\u73B0\uFF0C\u4F8B\u5982\u5B58\u53D6\u786C\u76D8\u3001\u7F51\u7EDC\u529F\u80FD\u3001CPU\u8D44\u6E90\u83B7\u53D6\u7B49\u3002\u901A\u5E38\u6307\u7684\u662F Linux \u5185\u6838\uFF0C\u76F8\u5BF9\u7684\u8FD8\u6709 Linux \u53D1\u884C\u7248\u6982\u5FF5\uFF0C\u53D1\u884C\u7248\u662F\u6307\u5728Linux\u5185\u6838\u7684\u57FA\u7840\u4E0A\uFF0C\u96C6\u6210\u4E86\u8F6F\u4EF6\u5305\u7BA1\u7406\u5668\u3001\u5C4F\u5E55\u7ED8\u56FE\u7684\u5E95\u5C42\u63A5\u53E3\u3001\u9AD8\u7EA7\u7684\u684C\u9762\u73AF\u5883\u7B49\u4E00\u7CFB\u5217\u8F6F\u4EF6\uFF0C\u4FBF\u4E8E\u7528\u6237\u4F7F\u7528\u3002</li><li>shell\uFF1A\u4E2D\u6587\u610F\u601D\u662F\u201C\u58F3\u7A0B\u5E8F\u201D\uFF0C\u6307\u7684\u662F\u80FD\u5BF9\u64CD\u4F5C\u7CFB\u7EDF\u548C\u5E94\u7528\u7A0B\u5E8F\u8FDB\u884C\u64CD\u4F5C\u7684\u63A5\u53E3\u7A0B\u5E8F\uFF0C\u72ED\u4E49\u7684\u58F3\u7A0B\u5E8F\u6307\u7684\u662F\u547D\u4EE4\u884C\u65B9\u9762\u7684\u8F6F\u4EF6\uFF0C\u4F8B\u5982bash;\u5E7F\u4E49\u4E0A\u4E5F\u5305\u62EC\u56FE\u5F62\u754C\u9762\u4E0B\u7684\u7A0B\u5E8F\u3002shell\u8D1F\u8D23\u89E3\u91CA\u7528\u6237\u8F93\u5165\u7684\u547D\u4EE4\u4F20\u8F93\u5230linux\u5185\u6838\u6267\u884C\uFF0C\u5728\u5386\u53F2\u7684\u957F\u6CB3\u4E2D\uFF0C\u4F5C\u4E3A\u89E3\u91CA\u7A0B\u5E8F\u7684\u5B9E\u73B0\u6709\u5F88\u591A\u79CD\uFF0Cbash\u662F\u5176\u5B83\u6700\u5E7F\u6CDB\u4F7F\u7528\u7684\u4E00\u79CD\uFF0C\u4E5F\u662F Linux \u53D1\u884C\u7248\u4E2D\u9ED8\u8BA4\u81EA\u5E26\u7684\u89E3\u91CA\u7A0B\u5E8F\u3002\u53EF\u4EE5\u7406\u89E3 shell \u662F\u5404\u79CD\u547D\u4EE4\u884C\u89E3\u91CA\u7A0B\u5E8F\u7684\u7EDF\u79F0\u3002</li></ul><blockquote><p>Unix shell\uFF1A\u4E00\u79CD\u58F3\u5C42\u4E0E\u547D\u4EE4\u884C\u754C\u9762\uFF0C\u662FUnix\u64CD\u4F5C\u7CFB\u7EDF\u4E0B\u4F20\u7EDF\u7684\u7528\u6237\u548C\u8BA1\u7B97\u673A\u7684\u4EA4\u4E92\u754C\u9762\u3002\u666E\u901A\u610F\u4E49\u4E0A\u7684shell\u5C31\u662F\u53EF\u4EE5\u63A5\u53D7\u7528\u6237\u8F93\u5165\u547D\u4EE4\u7684\u7A0B\u5E8F\u3002\u5B83\u4E4B\u6240\u4EE5\u88AB\u79F0\u4F5Cshell\u662F\u56E0\u4E3A\u5B83\u9690\u85CF\u4E86\u64CD\u4F5C\u7CFB\u7EDF\u4F4E\u5C42\u7684\u7EC6\u8282\u3002Unix\u64CD\u4F5C\u7CFB\u7EDF\u4E0B\u7684shell\u65E2\u662F\u7528\u6237\u4EA4\u4E92\u7684\u754C\u9762\uFF0C\u4E5F\u662F\u63A7\u5236\u7CFB\u7EDF\u7684\u811A\u672C\u8BED\u8A00\u3002</p></blockquote><blockquote><p>shell\u547D\u4EE4\uFF1A\u53EF\u4EE5\u8BA9shell\u5DE5\u5177\u89E3\u91CA\u7684\u547D\u4EE4\uFF0C\u7EDD\u5927\u90E8\u5206\u662FLinux\u64CD\u4F5C\u547D\u4EE4\u548C\u90E8\u5206shell\u81EA\u8EAB\u547D\u4EE4\u3002shell\u811A\u672C\uFF1A\u4E00\u7CFB\u5217shell\u547D\u4EE4\u7684\u96C6\u5408</p></blockquote><ul><li>terminal: \u7EC8\u7AEF\u3002\u5728\u65E9\u671F Linux \u64CD\u4F5C\u7CFB\u7EDF\u4E2D\u5F00\u673A\u76F4\u63A5\u663E\u793A\u7684 shell \u64CD\u4F5C\u754C\u9762\uFF0C\u4F46\u5728\u5404\u5F0F\u5404\u6837\u7684\u56FE\u5F62\u5316\u64CD\u4F5C\u754C\u9762\u7684Linux\u53D1\u884C\u7248\uFF0C\u5F00\u673A\u5E76\u4E0D\u662F\u547D\u4EE4\u884C\u754C\u9762\u4E86\uFF0C\u6240\u4EE5\u9700\u8981\u4E00\u5904\u7EC8\u7AEF\u7A0B\u5E8F\u5F00\u542F\u547D\u4EE4\u884C\u754C\u9762\uFF0C\u7EC8\u7AEF\u4E5F\u662F\u4E00\u4E2A\u5DE5\u5177\u7A0B\u5E8F\u3002</li></ul><p>\u53C2\u8003\u94FE\u63A5\uFF1A</p><ul><li><a href="https://blog.csdn.net/weixin_39212776/article/details/81079727" target="_blank" rel="noopener noreferrer">Linux\u4E4Bshell\u4EE5\u53CAbash\u3001dash\u7684\u8BE6\u89E3</a> ---- \u91CC\u9762\u4E24\u5F20\u56FE\u6BD4\u8F83\u5408\u9002</li><li><a href="https://www.cnblogs.com/jiading/p/11826773.html" target="_blank" rel="noopener noreferrer">shell\u3001bash\u3001terminal\u548Ckernel\u4E4B\u95F4\u7684\u5173\u7CFB</a></li></ul><h2 id="git\u3001git-bash\u548Cgit-shell\u6709\u4EC0\u4E48\u533A\u522B\uFF1F" tabindex="-1">git\u3001git bash\u548Cgit shell\u6709\u4EC0\u4E48\u533A\u522B\uFF1F <a class="header-anchor" href="#git\u3001git-bash\u548Cgit-shell\u6709\u4EC0\u4E48\u533A\u522B\uFF1F" aria-hidden="true">#</a></h2><p><a href="https://www.zhihu.com/question/34582452" target="_blank" rel="noopener noreferrer">git\u3001git bash\u548Cgit shell\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</a></p><ul><li>git \u662F\u7248\u672C\u63A7\u5236\u5DE5\u5177\uFF0C\u6700\u5F00\u59CB\u8BBE\u8BA1\u7528\u4E8E\u5728Unix\u98CE\u683C\u7684\u547D\u4EE4\u884C\u73AF\u5883\u4E2D\u6267\u884C\u3002\u50CF Linux \u548C macOS \u8FD9\u6837\u7684\u64CD\u4F5C\u7CFB\u7EDF\u90FD\u5305\u542B\u5185\u7F6E\u7684 Unix \u547D\u4EE4\u884C\u7EC8\u7AEF\uFF0C\u6240\u4EE5\u5728\u547D\u4EE4\u884C\u4E2D\u7528 git \u505A\u7248\u672C\u63A7\u5236\u5C31\u975E\u5E38\u65B9\u4FBF\u3002\u7136\u800C\uFF0CWindows \u4F7F\u7528 Windows \u547D\u4EE4\u63D0\u793A\u7B26\uFF0C\u5185\u7F6E\u7684cmd\u547D\u4EE4\u884C\u7EC8\u7AEF\uFF0C\u8FD9\u662F\u4E00\u4E2A\u975EUnix\u7EC8\u7AEF\u73AF\u5883\uFF0C\u6240\u4EE5\u6211\u4EEC\u5982\u679C\u9700\u8981\u5728windows\u7CFB\u7EDF\u4E0A\u4F7F\u7528git\u529F\u80FD\uFF0C\u5C31\u9700\u8981\u4F7F\u7528git-bash\u3002</li><li>git bash \u662F\u4E00\u4E2A\u9002\u7528\u4E8E Windows \u7CFB\u7EDF\u73AF\u5883\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5B83\u4E3A git \u547D\u4EE4\u884C\u64CD\u4F5C\u63D0\u4F9B\u4E86\u4E00\u4E2A\u4EFF\u771F\u5C42\u3002\u76F8\u5F53\u4E8E\u5728windows\u4E0A\u901A\u8FC7 git bash \u8FD9\u4E2A\u7A0B\u5E8F\u6A21\u62DFUnix \u547D\u4EE4\u884C\u7EC8\u7AEF\u51FA\u6765\uFF0C\u7136\u540E\u5728\u8FD9\u4E2A\u4EFF\u771F\u7EC8\u7AEF\u91CC\u9762\u505Agit \u76F8\u5173\u7684\u7248\u672C\u63A7\u5236\u529F\u80FD\u3002</li><li>git shell \u662F\u4E00\u4E2A\u901A\u8FC7 SSH \u8BBF\u95EE git \u670D\u52A1\u5668\u7684\u7A0B\u5E8F\uFF0C\u6BD4\u5982\u8BF4\u4F60\u628A\u4E00\u4E2A\u4E3B\u673A\u4F5C\u4E3A git \u670D\u52A1\u5668\uFF0C\u7136\u540E\u901A\u8FC7 git shell \u8FDB\u884C SSH \u8FDE\u63A5\u5230\u670D\u52A1\u5668\uFF0C\u5E76\u628A\u670D\u52A1\u5668\u7528\u4F5C\u6258\u7BA1 git \u5B58\u50A8\u5E93\u3002git shell \u4E00\u822C\u53EA\u5141\u8BB8\u4F7F\u7528\u670D\u52A1\u5668\u64CD\u4F5Cgit\u529F\u80FD\uFF0C\u5176\u5B83\u884C\u4E3A\u662F\u88AB\u9650\u5236\u7684\u3002</li></ul><h2 id="bin-bash-\u548C-usr-bin-bash-\u548C-usr-bin-env-bash-\u548C-usr-bin-env-node-\u7684\u6BD4\u8F83" tabindex="-1">#!/bin/bash \u548C #!/usr/bin/bash \u548C #!/usr/bin/env bash \u548C #!/usr/bin/env node \u7684\u6BD4\u8F83 <a class="header-anchor" href="#bin-bash-\u548C-usr-bin-bash-\u548C-usr-bin-env-bash-\u548C-usr-bin-env-node-\u7684\u6BD4\u8F83" aria-hidden="true">#</a></h2><ul><li>\u6B64\u884C\u79F0\u4E3A shebang\uFF08\u5C31\u662F sharp (#) + bang (!) \u7684\u610F\u601D\uFF09\uFF0C\u4F1A\u6307\u5F15\u64CD\u4F5C\u7CFB\u7EDF\u4F7F\u7528\u63A5\u4E0B\u6765\u6307\u5B9A\u7684\u7A0B\u5E8F\u8FD0\u884C\u6B64\u6587\u4EF6\uFF0C\u4F7F\u7528\u7EDD\u5BF9\u8DEF\u5F84\u3002\u5728linux\u7684bash\u7684\u811A\u672C\uFF0C\u9700\u5728\u5F00\u5934\u4E00\u884C\u6307\u5B9A\u811A\u672C\u7684\u89E3\u91CA\u7A0B\u5E8F\u3002</li><li>env\u547D\u4EE4\u53EF\u4EE5\u5728\u7CFB\u7EDF\u7684PATH\u76EE\u5F55\u4E2D\u67E5\u627E\u9884\u8BBE\u7684\u7CFB\u7EDF\u73AF\u5883\u53D8\u91CF\uFF0C\u8FD9\u4E2A\u547D\u4EE4\u603B\u662F\u5728/usr/bin\u76EE\u5F55\u3002</li></ul><blockquote><p>#!/usr/bin/env NAME\u8FD9\u4E2A\u8BED\u6CD5\u7684\u610F\u601D\u662F\uFF0C\u8BA9 Shell \u67E5\u627E$PATH\u73AF\u5883\u53D8\u91CF\u91CC\u9762\u7B2C\u4E00\u4E2A\u5339\u914D\u7684NAME\u3002\u5982\u679C\u4F60\u4E0D\u77E5\u9053\u67D0\u4E2A\u547D\u4EE4\u7684\u5177\u4F53\u8DEF\u5F84\uFF0C\u6216\u8005\u5E0C\u671B\u517C\u5BB9\u5176\u4ED6\u7528\u6237\u7684\u673A\u5668\uFF0C\u8FD9\u6837\u7684\u5199\u6CD5\u5C31\u5F88\u6709\u7528\u3002<br> /usr/bin/env bash\u7684\u610F\u601D\u5C31\u662F\uFF0C\u8FD4\u56DEbash\u53EF\u6267\u884C\u6587\u4EF6\u7684\u4F4D\u7F6E\uFF0C\u524D\u63D0\u662Fbash\u7684\u8DEF\u5F84\u662F\u5728$PATH\u91CC\u9762\u3002\u5176\u4ED6\u811A\u672C\u6587\u4EF6\u4E5F\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E2A\u547D\u4EE4\u3002\u6BD4\u5982 Node.js \u811A\u672C\u7684 Shebang \u884C\uFF0C\u53EF\u4EE5\u5199\u6210\u8FD9\u6837 /usr/bin/env node\u3002</p></blockquote><p>\u5982\u679C\u786E\u5B9A\u811A\u672C\u89E3\u91CA\u7A0B\u5E8F\u7684\u8DEF\u5F84\uFF0C\u53EF\u4EE5\u76F4\u63A5\u5199\u6B7B\u5B8C\u6574\u8DEF\u5F84\u3002\u5982 #!/bin/bash \u548C #!/usr/bin/bash\u3002\u4F46\u811A\u672C\u7A0B\u5E8F\u5728\u4E0D\u540C\u7684\u7CFB\u7EDF\u4E2D\u6267\u884C\uFF0C\u4E0D\u80FD\u4FDD\u8BC1\u89E3\u91CA\u7A0B\u5E8F\u90FD\u5B89\u88C5\u5728\u540C\u6837\u7684\u7EDD\u5BF9\u8DEF\u5F84\u4E2D\uFF0C\u56E0\u4E3A\u5728\u4E0D\u540C\u7684\u7CFB\u7EDF\uFF0C\u547D\u4EE4\u6216\u7A0B\u5E8F\u5B58\u653E\u7684\u4F4D\u7F6E\u53EF\u80FD\u4E0D\u540C\uFF0C\u4F46\u662F\u5927\u90E8\u5206\u7A0B\u5E8F\u90FD\u4F1A\u5728 $PATH\u4E2D\u8BBE\u7F6E\u73AF\u5883\u53D8\u91CF\uFF0C\u8FD9\u65F6\u4E3A\u4E86\u80FD\u5728\u4E0D\u540C\u7684\u7CFB\u7EDF\u4E0A\u5177\u6709\u66F4\u5927\u7684\u901A\u7528\u6027\uFF0C\u4E0D\u5199\u6B7B\u8DEF\u5F84\uFF0C\u800C\u662F\u4ECE\u7CFB\u7EDF\u5B89\u88C5\u7684\u73AF\u5883\u53D8\u91CF\u4E2D\u67E5\u7684\u89E3\u91CA\u7A0B\u5E8F\u7684\u8DEF\u5F84\u3002\u8FD9\u65F6\u53EF\u4EE5\u4F7F\u7528 #!/usr/bin/env bash \u548C #!/usr/bin/env node</p><h2 id="package-json-\u4E2D\u7684-bin-\u5B57\u6BB5" tabindex="-1">package.json \u4E2D\u7684 bin \u5B57\u6BB5 <a class="header-anchor" href="#package-json-\u4E2D\u7684-bin-\u5B57\u6BB5" aria-hidden="true">#</a></h2><ul><li>\u4E3A\u4EC0\u4E48\u53EF\u4EE5\u76F4\u63A5 <code>npm run xxx</code> \u8FD0\u884C package.json \u6587\u4EF6\u4E2D script \u5B57\u6BB5\u5B9A\u4E49\u7684\u547D\u4EE4\uFF1F</li><li>package.json \u4E2D\u7684 bin \u5B57\u6BB5\u4F5C\u7528</li></ul><p><code>npm run</code> \u4F1A\u521B\u5EFA\u4E00\u4E2A shell\uFF0C\u6267\u884C\u6307\u5B9A\u7684\u547D\u4EE4\u3002\u4F46\u6267\u884C\u547D\u4EE4\u524D\uFF0C<code>npm run</code>\u547D\u4EE4\u4F1A\u5148\u81EA\u52A8\u4E34\u65F6\u5C06 <code>node_modules/.bin</code> \u8DEF\u5F84\u52A0\u5165\u73AF\u5883\u53D8\u91CF $PATH \u4E2D\uFF0C\u6240\u4EE5 scripts \u5B57\u6BB5\u91CC\u9762\u8C03\u7528\u547D\u4EE4\u65F6\u4E0D\u7528\u52A0\u4E0A\u8DEF\u5F84\uFF0C\u53EF\u4EE5\u76F4\u63A5\u8FD0\u884C\uFF0C\u8FD9\u5C31\u907F\u514D\u4E86\u5168\u5C40\u5B89\u88C5NPM\u6A21\u5757\u3002</p><div class="language-json"><pre><code><span class="token property">&quot;scripts&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
  <span class="token property">&quot;eslint&quot;</span><span class="token operator">:</span> <span class="token string">&quot;./node_modules/.bin/eslint .&quot;</span><span class="token punctuation">,</span>
  <span class="token comment">// \u53EF\u4EE5\u7B80\u5199\u6210</span>
  <span class="token property">&quot;eslint&quot;</span><span class="token operator">:</span> <span class="token string">&quot;eslint .&quot;</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\u800C <code>node_modules/.bin</code> \u76EE\u5F55\u662F\u968F <code>node_modules</code> \u76EE\u5F55\u81EA\u52A8\u521B\u5EFA\u7684\u3002\u5F53\u4F7F\u7528 <code>npm install</code> \u5B89\u88C5\u4E00\u4E2A\u5305\u65F6\uFF0C\u9664\u4E86\u5C06\u5305\u4EE3\u7801\u653E\u5728 <code>node_modules</code>\u76EE\u5F55\u4E2D\uFF0C\u8FD8\u662F\u67E5\u627E\u8BE5\u5305\u7684 <code>package.json</code> \u6587\u4EF6\u4E2D\u662F\u5426\u6709\u5B9A\u4E49 <code>bin</code> \u5B57\u6BB5\uFF0C\u5982\u679C\u6709\uFF0C\u4F1A\u4F7F\u7528 <code>bin</code> \u5B57\u6BB5\u7684\u503C\u5728 <code>node_modules/.bin</code> \u76EE\u5F55\u4F1A\u751F\u6210\u4E24\u4E2A\u6587\u4EF6<code>bash \u6587\u4EF6\u548C.cmd \u6587\u4EF6</code>\uFF0C\u6307\u5411ESLint\u6A21\u5757\u7684\u53EF\u6267\u884C\u811A\u672C\u3002</p><p>\u5982\u679C\u672C\u5730\u5F00\u53D1npm \u7684\u5305\uFF0C\u9700\u8981\u5728\u9879\u76EE\u4E2D\u8C03\u8BD5\u4F7F\u7528\uFF0C\u53EF\u4EE5\u4F7F\u7528 <code>npm link</code> \u624B\u52A8\u5EFA\u7ACB\u8F6F\u94FE\uFF0C\u8FD9\u6837\u9879\u76EE\u53EF\u4EE5\u4F7F\u7528 <code>require(&#39;xxx&#39;)</code> \u5F15\u5165\uFF0C\u8FDB\u884C\u8C03\u8BD5\u5F00\u53D1\u3002 <a href="https://javascript.ruanyifeng.com/nodejs/npm.html#toc18" target="_blank" rel="noopener noreferrer">\u5177\u4F53 npm link</a></p><p>\u53C2\u8003\u94FE\u63A5\uFF1A</p><ul><li><a href="http://smilejay.com/2012/03/linux_shebang/" target="_blank" rel="noopener noreferrer">LINUX\u4E0A\u7684SHEBANG\u7B26\u53F7(#!)</a></li><li><a href="https://wangdoc.com/bash/script.html#shebang-%E8%A1%8C" target="_blank" rel="noopener noreferrer">bash\u6559\u7A0B Shebang\u884C</a></li></ul>`,33),o=[a];function r(t,u,h,b,c,d){return i(),e("div",null,o)}var g=n(l,[["render",r]]);export{x as __pageData,g as default};
